mapper.delete:

delete from tb_daily_input
where date_ = #{date,jdbcType=TIMESTAMP}
  and utm_source_id = #{utmSourceId,jdbcType=INTEGER}

mapper.update:

update tb_daily_input
set consume = #{consume,jdbcType=DECIMAL},
  discount = #{discount,jdbcType=REAL},
  display = #{display,jdbcType=INTEGER},
   exposure = #{exposure,jdbcType=INTEGER},
  click = #{click,jdbcType=INTEGER},
  comment = #{comment,jdbcType=VARCHAR},
  downloads = #{downloads,jdbcType=INTEGER}
where date_ = #{date,jdbcType=TIMESTAMP}
  and utm_source_id = #{utmSourceId,jdbcType=INTEGER}

mapper.select:

select date_, utm_source_id, consume, discount, display,exposure, click,comment,downloads
from tb_daily_input
where date_ = #{date,jdbcType=VARCHAR}
  and utm_source_id = #{utmSourceId,jdbcType=INTEGER}


	select d.date_, t.description,d.utm_source_id, d.consume, d.discount, d.display,d.exposure, d.click,d.comment,d.downloads
    from tb_daily_input d RIGHT JOIN tb_utm_source t on d.utm_source_id = t.id
    where d.date_ = #{date,jdbcType=TIMESTAMP}
      and t.description = #{description,jdbcType=VARCHAR}

select date_, utm_source_id, consume, discount, display,exposure, click, comment,downloads
from tb_daily_input
where date_=#{date,jdbcType=DATE}
